import React, { useState } from 'react';
import { View, Text, Button, Image, StyleSheet, SafeAreaView } from 'react-native';
import ImagePicker from 'react-native-image-picker';

function ToDoItem({ task, handleDelete }) {
    const [completed, setCompleted] = useState(false);
    const [image, setImage] = useState(null);

    const handleComplete = () => {
        setCompleted(!completed);
    }

    const selectImage = () => {
        const options = {
            noData: true,
        };
        ImagePicker.launchImageLibrary(options, response => {
            if (response.uri) {
                setImage(response.uri);
            }
        });
    };

    return (
        <SafeAreaView style={styles.itemContainer}>
            <Text style={completed ? styles.completedTask : null}>{task}</Text>
            {image && (
                <Image
                    source={{ uri: image }}
                    style={{ width: 50, height: 50 }}
                />
            )}
            <View style={styles.buttonsContainer}>
                <Button title="Select Image" onPress={selectImage} color="purple"/>
                <View style={styles.space} />
                <Button title="Completed" onPress={handleComplete} color={completed ? "green" : null} />
                <View style={styles.space} />
                <Button title="Delete" onPress={handleDelete} color="red" />
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    itemContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginVertical: 10,
    },
    buttonsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    space: {
        width: 20,
    },
    completedTask: {
        textDecorationLine: 'line-through',
        color: 'green',
    },
});

export default ToDoItem;
