import React, { useState } from 'react';
import { View, Text, Button, StyleSheet, Image, Modal } from 'react-native';

function ToDoItem({ task, image, handleDelete, handleCamera }) {
    const [completed, setCompleted] = useState(false);
    const [modalVisible, setModalVisible] = useState(false);

    const handleComplete = () => {
        setCompleted(!completed);
    }

    return (
        <View style={styles.itemContainer}>
            <Text style={completed ? styles.completedTask : null}>{task}</Text>
            <View style={styles.buttonsContainer}>
                <Button title="Camera" onPress={handleCamera} />
                <View style={styles.space} />
                <Button title="Completed" onPress={handleComplete} color={completed ? "green" : null} />
                <View style={styles.space} />
                <Button title="Remark" onPress={() => setModalVisible(true)} disabled={!image} />
                <View style={styles.space} />
                <Button title="Delete" onPress={handleDelete} color="red" />
            </View>
            <Modal animationType="slide" transparent={true} visible={modalVisible}>
                <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                        {image ? (
                            <Image source={{ uri: image }} style={styles.image} />
                        ) : (
                            <Text>No Image</Text>
                        )}
                        <Button title="Close" onPress={() => setModalVisible(false)} />
                    </View>
                </View>
            </Modal>
        </View>
    );
}

const styles = StyleSheet.create({
    itemContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginVertical: 10,
    },
    buttonsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    space: {
        width: 20,
    },
    completedTask: {
        textDecorationLine: 'line-through',
        color: 'green',
    },
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
    modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },
    image: {
        width: 300,
        height: 300,
    }
});

export default ToDoItem;
